from abc import ABC, abstractmethod

class Forma(ABC):
    @abstractmethod
    def desenhar(self):
        pass

class Circulo(Forma):
    def desenhar(self):
        print("Desenhando um Círculo.")

class Quadrado(Forma):
    def desenhar(self):
        print("Desenhando um Quadrado.")

class FabricaDeFormas:
    @classmethod
    def criar_forma(cls, tipo):
        if tipo.lower() == 'circulo':
            return Circulo()
        elif tipo.lower() == 'quadrado':
            return Quadrado()
        else:
            raise ValueError(f"Tipo de forma desconhecido: {tipo}")

# Teste
forma1 = FabricaDeFormas.criar_forma('circulo')
forma1.desenhar()
forma2 = FabricaDeFormas.criar_forma('quadrado')
