class Subject:
    def __init__(self):
        self._observers = []

    def attach(self, observer):
        if observer not in self._observers:
            self._observers.append(observer)
            print(f"Subject: Observer {observer.nome} anexado.")

    def detach(self, observer):
        try:
            self._observers.remove(observer)
            print(f"Subject: Observer {observer.nome} desanexado.")
        except ValueError:
            print(f"Subject: Observer {observer.nome} não encontrado para desanexar.")

    def notify(self, mensagem):
        print(f"Subject: Notificando observadores com '{mensagem}'")
        for observer in self._observers:
            observer.update(mensagem)

class Observer:
    def __init__(self, nome):
        self.nome = nome

    def update(self, mensagem):
        print(f"Observer {self.nome}: Recebi a mensagem -> {mensagem}")

