class MinhaMeta(type):
    def __new__(cls, name, bases, dct):
        # Adiciona o atributo 'criado_por' ao dicionário da classe
        dct['criado_por'] = 'MinhaMeta'
        # Chama o __new__ da metaclasse base (type) para criar a classe
        return super().__new__(cls, name, bases, dct)

# Define a classe MinhaClasse usando a metaclasse
class MinhaClasse(metaclass=MinhaMeta):
    pass

# Imprime o atributo adicionado pela metaclasse
print(MinhaClasse.criado_por)

class ValidarAtributosMeta(type):
    def __new__(cls, name, bases, dct):
        for attr_name, attr_value in dct.items():
            # Verifica se o nome começa com '_' mas não com '__'
            # e se não é um método (callable)
            if attr_name.startswith('_') and not attr_name.startswith('__') and not callable(attr_value):
                raise TypeError(f"Atributo '{attr_name}' na classe '{name}' não deve começar com um único underscore.")

        return super().__new__(cls, name, bases, dct)

