import functools

def log_io(prefixo):
    def decorator(func):
        @functools.wraps(func)
        def wrapper(*args, **kwargs):
            # Formata os argumentos para impressão
            args_str = ', '.join(map(repr, args))
            kwargs_str = ', '.join(f'{k}={v!r}' for k, v in kwargs.items())
            all_args_str = ', '.join(filter(None, [args_str, kwargs_str]))

            print(f"{prefixo} Entrando em '{func.__name__}' com args=({all_args_str})")

            try:
                resultado = func(*args, **kwargs)
                print(f"{prefixo} Saindo de '{func.__name__}' com resultado={resultado!r}")
                return resultado
            except Exception as e:
                print(f"{prefixo} Exceção em '{func.__name__}': {e!r}")
                raise # Re-levanta a exceção

        return wrapper
    return decorator

# Aplique o decorador com um prefixo
@log_io(prefixo="[INFO]")
def somar(a, b):
    print("Executando soma...")
    return a + b

@log_io(prefixo="[DEBUG]")
def dividir(a, b):
    print("Executando divisão...")
    return a / b

resultado_soma = somar(10, b=5)
